class Utils {

  static showSnackBar(String? message,{bool? isSuccess = false}) {
    if (message == null) {
      return;
    }
    Get.closeAllSnackbars();
    Get.rawSnackbar(
      message: _extractText(message),
      padding: const EdgeInsets.only(left: 20, bottom: 13, top: 18, right: 30),
      margin: const EdgeInsets.fromLTRB(10, 0, 10, 10),
      backgroundColor: AppColor.white,
      borderRadius: 5,
      borderWidth: 1,
      borderColor: AppColor.blueTextColor.withOpacity(0.15),
      snackPosition: SnackPosition.TOP,
      messageText: Row(
        children: [
          isSuccess == true ? const Icon(
            Icons.check,color: AppColor.themeColor,
          ) : const SizedBox(),
          const SizedBox(width: 6,),
          Expanded(
            child: Text(
              _extractText(message),
              maxLines: 2,
              softWrap: false,
              textAlign: TextAlign.start,
              style: TextStyle(
                  overflow: TextOverflow.ellipsis,
                  color: AppColor.black,
                  fontWeight: FontWeight.w500,
                  fontSize: 14.sp,
                  height: 1.4),
            ),
          ),
        ],
      ),
    );
  }

