  static showSuccessAlert(String message) {
    CoolAlert.show(
      context: Get.context!,
      type: CoolAlertType.success,
      text: _extractText(message),
    );
  }



  static String capsF(String? text) {
    if (text == null || text.isEmpty) {
      return "";
    }
    return "${text[0].toUpperCase()}${text.substring(1)}";
  }

  static String _extractText(String? message) {
    if (message == null) {
      return "";
    }
    if (message.length > 200) {
      return message.substring(0, 199);
    } else {
      return message;
    }
  }

  static bool emailValidation(String email) {
    Pattern pattern =
        r'^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$';
    RegExp regex = RegExp(pattern.toString());
    return regex.hasMatch(email);
  }

  static bool passwordValidation(String password) {
    RegExp regExp = RegExp(
      r'^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*()_+={}\[\]|;:"<>,./?\\])[A-Za-z\d!@#$%^&*()_+={}\[\]|;:"<>,./?\\]{6,}$',
    );
    return regExp.hasMatch(password);
  }

