  static bool linkValidation(String link) {
    RegExp regex = RegExp(
        r"^(https?:\/\/)?(www\.)?[a-zA-Z0-9]+([-.][a-zA-Z0-9]+)*\.[a-zA-Z]{2,}([/?].*)?$");
    return regex.hasMatch(link);
  }

  static bool validateURL(String url) {
    RegExp regex = RegExp(
        r"^(https?:\/\/)?(www\.)?[a-zA-Z0-9]+([-.][a-zA-Z0-9]+)*\.[a-zA-Z]{2,}([/?].*)?$");
    return regex.hasMatch(url);
  }

