  static Future<bool> hasNetwork() async {
    var connectivityResult = await (Connectivity().checkConnectivity());
    if (connectivityResult == ConnectivityResult.none) {
      Utils.showSnackBar("Please check your internet connection");
      return false;
    } else {
      return true;
    }
  }

  static String getFullName(String? firstName, String? lastName) {
    if (firstName != null && lastName != null) {
      return "$firstName $lastName";
    } else if (firstName != null) {
      return firstName;
    } else {
      return "";
    }
  }

  static String maskCardNumber(String? cardNumber) {
    cardNumber = cardNumber!.replaceAll(" ", "");
    int var1 = 0;
    StringBuffer var2;
    for (var2 = StringBuffer(); var1 < cardNumber.length; ++var1) {
      if (var1 > cardNumber.length - 5) {
        var2.write(cardNumber[var1]);
      } else {
        var2.write("X");
      }
    }

    return var2.toString();
  }

  static String getYoutubeThumbnail(String videoUrl) {
    final Uri? uri = Uri.tryParse(videoUrl);
    if (uri == null) {
      return "null";
    }
    return 'https://img.youtube.com/vi/${uri.queryParameters['v']}/0.jpg';
  }

  static Future<void> openUrl(String url) async {
    await canLaunchUrlString(url)
        ? await launchUrlString(url, mode: LaunchMode.externalApplication)
        : showSnackBar('Could not launch $url');
  }

  static bool userNameValidation(String userName) {
    String pattern = r'^[a-zA-Z0-9_-]{6,30}$';

    RegExp regex = RegExp(pattern);
    return regex.hasMatch(userName);
  }

  static String? getYoutubeVideoIdByURL(String url) {
    final regex = RegExp(r'.*\?v=(.+?)($|[\&])', caseSensitive: false);

    try {
      if (regex.hasMatch(url)) {
        return regex.firstMatch(url)!.group(1);
      }
    } catch (e) {
      return null;
    }
    return null;
  }

