  static String? getFileType(String path) {
    final mimeType = lookupMimeType(path);
    String? result = mimeType?.substring(0, mimeType.indexOf('/'));
    return result;
  }

  static String uint8ListToBase64(Uint8List data) {
    return base64Encode(data);
  }

  static Uint8List base64ToUint8List(String base64String) {
    return base64Decode(base64String);
  }

  static String formatString(var value) {
    if (value == null) {
      return "0";
    }
    String formattedValue = value.toStringAsFixed(1);
    return formattedValue;
  }

  static launchAppStore() async {
    final url = Platform.isAndroid
        ? 'https://play.google.com/store/apps/details?id=com.example.app' // Replace with your Android app's package name
        : 'https://apps.apple.com/app/id123456789'; // Replace with your iOS app's App Store link

    if (await canLaunch(url)) {
      await launch(url);
    } else {
      throw 'Could not launch $url';
    }
  }

  static Future<DateTime?> openDatePicker(
      {DateTime? selectedDate, DateTime? startDate}) async {
    DateTime currentDate = DateTime.now();
    DateTime startOfYear = DateTime(currentDate.year, 1, 1);
    DateTime endOfYear =
        DateTime(currentDate.year, 12, 31, 23, 59, 59, 999, 999);

    final DateTime? picked = await showDatePicker(
      context: Get.context!,
      initialDate: startDate ?? selectedDate ?? currentDate,
      firstDate: startDate ?? startOfYear,
      lastDate: endOfYear,
      builder: (BuildContext? context, Widget? child) {
        return Theme(
          data: ThemeData.light().copyWith(
            primaryColor: AppColor.themeColor,
            colorScheme: const ColorScheme.light(primary: AppColor.themeColor),
            buttonTheme:
                const ButtonThemeData(textTheme: ButtonTextTheme.primary),
          ),
          child: child!,
        );
      },
    );
    if (picked != null) {
      return picked;
    } else {
      return null;
    }
  }

