  static String addHyphenAfterFourDigits(String input) {
    if (input.length <= 4)
      return input; // If the input has 4 or fewer characters, return it as it is

    String firstFourCharacters =
        input.substring(0, 4); // Extract the first four characters
    String remainingCharacters =
        input.substring(4); // Extract the remaining characters

    return '$firstFourCharacters-$remainingCharacters'; // Add a hyphen after the first four characters
  }

  static String getFAndLNameFirstCharacter(
      String? firstName, String? lastName) {
    String firstCharacter = firstName != null && firstName.isNotEmpty
        ? firstName[0].toUpperCase()
        : '';
    String lastCharacter = lastName != null && lastName.isNotEmpty
        ? lastName[0].toUpperCase()
        : '';
    return "$firstCharacter$lastCharacter";
  }

  static String getCapsFCharacter(String? input) {
    if (input != null && input.isNotEmpty) {
      String firstCharacter = input[0].toUpperCase();
      String remainingCharacters = input.substring(1);
      return "$firstCharacter$remainingCharacters";
    } else {
      return '';
    }
  }


  static Widget emptyBookingMessage(String? status) {
    String message;
    switch (status) {
      case 'upcoming':
        message = "No upcoming appointments";
        break;
      case 'finished':
        message = "No finished Appointments";
        break;
      default:
        message = "No booking found";
    }

    return Center(
      child: AppText(
        text: message,
        textSize: 16.sp,
        color: AppColor.themeColor,
        style: AppTextStyle.regular,
      ),
    );
  }

  static String checkNullOrEmpty(dynamic value) {
    if (value == null || value.toString().isEmpty) {
      return "0"; // Return "0" as a string if the value is null or empty
    } else {
      // Return the value as a string
      return value.toString();
    }
  }

  static String returnDouble(dynamic value) {
    if (value == null || value.toString().isEmpty) {
      return '0.0'; // Return "0.00" as a string if the value is null or empty
    } else {
      // Parse the value to double
      double parsedValue = double.parse(value.toString());

      // Format the double value with two decimal places
      String formattedValue = parsedValue.toStringAsFixed(2);

      return formattedValue; // Return the formatted value as a string
    }
  }



  static  bool checkValueIsNullOREmpty(var number) {
    if (number == null || number.isNaN) {
      return true;
    }
    return false;
  }

  static  bool hasDecimal(String input) {
    RegExp decimalRegex = RegExp(r'\.');
    return decimalRegex.hasMatch(input);
  }



  static bool checkValueNullOrEmpty(dynamic value) {
    if (value == null || value.toString().isEmpty) {
      return false;
    } else {
      return true;
    }
  }



  static (String, String) splitName(String fullName){
    List<String> nameParts = fullName.split(" ");

    String firstName = nameParts[0];
    String lastName = nameParts.length > 1 ? nameParts.sublist(1).join(" ") : "";
    return (firstName, lastName);
  }



